package myproject;

public class PrePost {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 3;
		i++;
		System.out.println(i);
		++i;
		System.out.println(i);
		System.out.println(++i);  //pre increment
		System.out.println(i++);  //post increment 
		System.out.println(i);

	}

}
